import React from "react";
//import SearchField from "./searchField";
import Background from "./img/background.jpg";
import NavBar from "./navbar";

class Edit extends React.Component {

    componentDidMount() {
        if(!this.props.location.state.id){
            this.props.history.push('/');
        }
    }

    handleSubmit = (event) => {
        console.log(event.target.oppsummering.value);
        console.log(event.target.beskrivelse.value);
        //this.props.history.pop('/overview');
    };

    handleClick = () => {
        this.props.history.push('/');
    };

    render() {
        return (
            <div>
                <img id="background" src={ Background } alt="" />
                <div className="navBar">
                </div>
                <NavBar />
                <div className="register_box" id="overview" >
                    <form className="register" onSubmit={this.handleSubmit}>
                        <h2 className="text-center text-white">Endre avvik</h2>
                        <br></br>
                        <p className="text-center text-white">Melding</p>
                        <input type="String" name="oppsummering" className="form-control"
                               value={JSON.parse(localStorage.getItem("issues")).PtSituationElement[(parseInt(this.props.location.state.id)-1)].Summary['@Value']}
                                required/>
                        <input type="String" name="beskrivelse" className="form-control"
                               value={JSON.parse(localStorage.getItem("issues")).PtSituationElement[(parseInt(this.props.location.state.id)-1)].Description['@Value']}/>
                        <br></br>
                        <p className="text-center text-white">Gyldighetsdato</p>
                        <div className="form-group d-flex">
                            <input type="datetime-local" name="from" className="form-control" readOnly="readonly"
                                   value={JSON.parse(localStorage.getItem("issues")).PtSituationElement[(parseInt(this.props.location.state.id)-1)].ValidityPeriod.StartTime.replace(":00+02:00", "")}/>
                            <input onChange={this.handleChangeDate} type="datetime-local" name="to" className="form-control"
                                   value={JSON.parse(localStorage.getItem("issues")).PtSituationElement[(parseInt(this.props.location.state.id)-1)].ValidityPeriod.EndTime.replace(":00+02:00", "")}
                                   required/>
                        </div>
                        <br></br>
                        <div className="submit justify-content-center">
                            <button type="submit" className="btn btn-primary btn-lg btn-block">Endre</button>
                            <button onClick={this.handleClick} type="submit" className="btn btn-warning btn-lg btn-block">Tilbake</button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export default Edit;
