const { createApolloFetch } = require('apollo-fetch');

const URI = 'https://api.staging.entur.io/journey-planner/v2/graphql';

const run = (authorities, line, date) => {
    const apolloFetch = createApolloFetch({ uri: URI, headers: { 'entur': 'deviation-messages' } });
    const lines = (date === null) ? '' : ', lines: "'+line+'"';
    const dates = (date === null) ? '' : ', activeDates: "'+date+'"';
    const partQuay = (date === null) ? '\n name\n quays {\n id\n name\n }' :
        '\n estimatedCalls(date:"'+date+'") {\n aimedDepartureTime\n quay {\n id\n name\n }\n }';
    const type = (date === null) ? 'lines' : 'serviceJourneys';

    const query = `{ ${type}(authorities:"${authorities}"${lines}${dates}) { id${partQuay} } } `;

    return apolloFetch({ query })
        .catch(error => error)
        .then(response => response)
};

const getLines = () => {
    const apolloFetch = createApolloFetch({ uri: URI, headers: { 'ET-client-Name': 'ops-center - ops-center' } });
    const query = `
      { 
        lines(authorities: ["NSB:Authority:NSB", "GOA:Authority:GOA"]) { 
          name 
          id
          quays {
            name
          }
        } 
      } `;

    return apolloFetch({ query })
      .catch(error => error)
      .then(response => response);
};

const getDepartures = (line, date) => {
    const apolloFetch = createApolloFetch({ uri: URI, headers: { 'ET-client-Name': 'ops-center - ops-center' } });
    const query = `
      { serviceJourneys(authorities: ["NSB:Authority:NSB", "GOA:Authority:GOA"], lines: "${line}", activeDates: "${date}") { 
        id
        estimatedCalls(date:"${date}") {
          aimedDepartureTime
          quay {
            name
            }
          }
        }
      }`;

    return apolloFetch({ query })
      .catch(error => error)
      .then(response => response);
};

const fetchGet = {
    method: 'GET',
    headers: {
        'accept': 'application/json',
        'ET-Client-Name': 'entur - deviation-messages'
    }
};

const organisationID = (id) => {
    fetch('https://api.staging.entur.io/organisations/v1/register/organisations/'+id, fetchGet)
        .catch (error => error)
        .then(response => response)
        .then(data => {
            localStorage.setItem('test', JSON.stringify(data));
        });
};

export default { run, organisationID }
