const { createApolloFetch } = require('apollo-fetch');

const uri = 'https://api.staging.entur.io/journey-planner/v2/graphql';

const run = (localKey, authorities, line, date) => {
    const apolloFetch = createApolloFetch({ uri });
    const lines = (date === null) ? '' : ', lines: "'+line+'"';
    const dates = (date === null) ? '' : ', activeDates: "'+date+'"';
    const partQuay = (date === null) ? '\n name\n quays {\n id\n name\n }' :
        '\n estimatedCalls(date:"'+date+'") {\n aimedDepartureTime\n quay {\n id\n name\n }\n }';
    const type = (date === null) ? 'lines' : 'serviceJourneys';

    const query = `{ ${type}(authorities:"${authorities}"${lines}${dates}) { id${partQuay} } } `;

    apolloFetch({ query })
        .catch(error => error)
        .then(response => {
            if(date === null){
                localStorage.setItem(localKey, JSON.stringify(response.data.lines));
            }else{
                localStorage.setItem(localKey, JSON.stringify(response.data.serviceJourneys));
            }
        });
};

const organisationID = () => {
    return fetch('https://course.dhis2.org/dhis/api/dataValueSets', {
        method: 'POST',
        credentials: 'include',
        mode: 'cors',
        headers: {
            Authorization: `Basic ${btoa('admin:district')}`,
            'Content-type': 'application/json',
            'Access-Control-Allow-Credentials': true
        },
        body: JSON.stringify(dataElement),
    })
        .catch(error => error)
        .then(response => response.json());
};

export default { run, organisationID }
