import React from "react";
const { createApolloFetch } = require('apollo-fetch');

const uri = 'https://api.staging.entur.io/journey-planner/v2/graphql';

const run = (localKey, authorities, date) => {
    const apolloFetch = createApolloFetch({ uri });
    let dates = null;
    let partQuay = '\n        name\n        quays {\n          id\n          name\n        }';
    let type = '';
    if(date === null){
        dates = '';
        type = 'lines';
    }else{
        type = 'serviceJourneys';
        name = '';
        dates = ', activeDates: "'+date+'"';
    }

    const query = `
      {
      ${type}(authorities:"${authorities}"${dates}) {
        id${partQuay}
      }
    }
    `;
    console.log(query);

    apolloFetch({ query })
        .catch(error => error)
        .then(response => {
            if(date === null){
                localStorage.setItem(localKey, JSON.stringify(response.data.lines));
            }else{
                if(response.data.serviceJourneys[0]){
                    localStorage.setItem(localKey, JSON.stringify(response.data.serviceJourneys));
                }else{
                    localStorage.setItem(localKey, JSON.stringify('No data'));
                }
            }
        });
};

export default { run }
