import React from "react";
const { createApolloFetch } = require('apollo-fetch');

const uri = 'https://api.staging.entur.io/journey-planner/v2/graphql';
const value = localStorage.getItem("loggedInAs")+':Authority:'+localStorage.getItem("loggedInAs");

const query = `
  {
  lines(authorities:"GOA:Authority:GOA") {
  	id
    name
    quays {
      id
      name
    }
  }
}
`;

const query2 = `
  {
  serviceJourneys(authorities: "GOA:Authority:GOA", activeDates: ["2020-01-15"]) {
    id
    quays {
      id
      name
    }
  }
}
`;

const run = (localKey, date) => {
    const apolloFetch = createApolloFetch({ uri });

    if(date === null){
        apolloFetch({ query })
            .catch(error => error)
            .then(response => {
                localStorage.setItem(localKey, JSON.stringify(response.data.lines));
            });
    }else{
        apolloFetch({ query })
            .catch(error => error)
            .then(response => {
                localStorage.setItem(localKey, JSON.stringify(response.data));
            });
    }
};

export default { run }
