import React from "react";
import NavBar from "./navbar";
import red from "./img/red.png";
import green from "./img/green.png"
import Background from "./img/background.jpg";

class Overview extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            date: '',
        };
        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {
        var that = this;
        var date = new Date().getDate();
        var month = new Date().getMonth() + 1; if(month < 10){ month = "0"+month }
        var year = new Date().getFullYear();
        var hours = new Date().getHours(); if(hours < 10){ hours = "0"+hours }
        var min = new Date().getMinutes(); if(min < 10){ min = "0"+min }
        var sec = new Date().getSeconds(); if(sec < 10){ sec = "0"+sec }
        that.setState({
            date: year + '-' + month + '-' + date +'T' + hours + ':' + min + ':' + sec+"+02:00",
        });
    }

    handleClick(event) {
        this.props.history.push('/');
    }

    returnRedOrGreenIcon(param) {
        if(Date.parse(param) < Date.parse(this.state.date)){
            return <img src={red} alt="" height="30" width="30" />
        }else {
            return <img src={green} alt="" height="30" width="30"/>
        }
    }

    getType(param) {
        if(param != null){
            if(Object.keys(param)[0] === "Networks"){
                return "Linje"
            }
            if(Object.keys(param)[0] === "StopPoints"){
                return "Stopp"
            }
            if(Object.keys(param)[0] === "VehicleJourneys"){
                return "Avgang"
            }
            else{
                return "Error"
            }
        }else{
            return "Error"
        }
    }

    getDate(param) {
        var datePart = param.match(/\d+/g),
            year = datePart[0].substring(2), // get only two digits
            month = datePart[1], day = datePart[2],
            hour = datePart[3], minutes = datePart[4];

        return hour+':'+minutes+'  '+day+'.'+month+'.'+year;
    }

    edit = (event) => {
        const table = document.getElementById("example");
        const tr = table.getElementsByTagName('tr')[event.target.value];
        const td = tr.getElementsByTagName("td")[2];
        td.innerHTML.replace("td ", "td contenteditable=\"true\" ");
        const melding = td.innerHTML;
        console.log(td);
    };

    render() {
        return (
            <div>
                <img id="background" src={ Background } alt="" />
                <div className="navBar">
                </div>
                <NavBar />
                <div className="register_box" id="overview" >
                    <h2 className="text-center text-white">Oversikt</h2>
                    <br></br>
                    <div className="table-responsive-md">
                        <table id="example" className="table table-dark table-striped" >
                            <thead>
                            <tr>
                                <th scope="col">Status</th>
                                <th scope="col">#</th>
                                <th scope="col">Melding</th>
                                <th scope="col">Gyldighet</th>
                                <th scope="col">Type</th>
                                <th scope="col"></th>
                            </tr>
                            </thead>
                            <tbody>
                            {(JSON.parse(localStorage.getItem("issues"))).PtSituationElement.map((item, key) => (
                                <tr key={item.SituationNumber}>
                                    <td className='Status'>{this.returnRedOrGreenIcon(item.ValidityPeriod.EndTime)}</td>
                                    <td className='#'>{item.SituationNumber.split(":").pop()}</td>
                                    <td className='Melding' width="30%">{item.Summary['@Value']}</td>
                                    <td className='Gyldighet'>{this.getDate(item.ValidityPeriod.StartTime)}<br></br>{this.getDate(item.ValidityPeriod.EndTime)}</td>
                                    <td className='Type'>{this.getType(item.Affects)}</td>
                                    <td><button value={item.SituationNumber.split(":").pop()} onClick={this.edit}>Endre</button></td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                    <button onClick={this.handleClick} type="submit" className="btn btn-warning btn-lg btn-block">Tilbake</button>
                </div>
            </div>
        );
    }
}

export default Overview;
