import React from 'react';
import ReactDOM from 'react-dom';
import './style/index.css';
import 'bootstrap/dist/css/bootstrap.css';
import { Route, BrowserRouter as Router } from 'react-router-dom'
import Home from './components/home'
import Overview from "./components/overview";
import Register from './components/register';
import Edit from './components/edit';
import auth from './authenticate/auth'
import api from "./api/api";
var convert = require('xml-js');


const firebase = require("firebase/app");
require("firebase/firestore");

var firebaseConfig = {
    apiKey: "AIzaSyCKcRzWRYlZkwG4I4WTTb2uRnI7VczCX5M",
    authDomain: "deviation-messages.firebaseapp.com",
    databaseURL: "https://deviation-messages.firebaseio.com",
    projectId: "deviation-messages",
    storageBucket: "deviation-messages.appspot.com",
    messagingSenderId: "132641363074",
    appId: "1:132641363074:web:1069e5f0a9717b4c"
};

firebase.initializeApp(firebaseConfig);
var db = firebase.firestore();

class App extends React.Component {
    state = {
        data: '',
        key: 'GOA:Authority:GOA',
        lines: '',
        id: '',
    };

    componentDidMount() {
        //api.organisationID(userInfo.id);

        let date = new Date().getDate(); if(date < 10){ date = "0"+date }
        let month = new Date().getMonth() + 1; if(month < 10){ month = "0"+month }
        let year = new Date().getFullYear();
        let hours = new Date().getHours(); if(hours < 10){ hours = "0"+hours }
        let min = new Date().getMinutes(); if(min < 10){ min = "0"+min }
        let sec = new Date().getSeconds(); if(sec < 10){ sec = "0"+sec }

        const dateTime = year + '-' + month + '-' + date +'T' + hours + ':' + min + ':' + sec+"+02:00";

        var siri = {
            Siri: {
                _attributes: {
                    version: "2.0" ,
                    xmlns: "http://www.siri.org.uk/siri",
                    'xmlns:ns2': "http://www.ifopt.org.uk/acsb",
                    'xmlns:ns3': "http://www.ifopt.org.uk/ifopt",
                    'xmlns:ns4': "http://datex2.eu/schema/2_0RC1/2_0"
                },
            }
        };

        var array = {
            ResponseTimestamp: dateTime,
            ProducerRef: "ENT",
            SituationExchangeDelivery: {
                ResponseTimestamp: dateTime,
                Situations: [],
            }
        };

        db.collection(this.state.key).get().then((querySnapshot) => {
            querySnapshot.forEach((doc) => {
                if(doc.id === 'Issues'){
                    if(doc.data().PtSituationElement){
                        this.setState({
                            data: doc.data(),
                            id: doc.id,
                        });
                        var situations = {PtSituationElement: []};
                        for (var i = 0; i < doc.data().PtSituationElement.length; i++) {
                            if(Date.parse(doc.data().PtSituationElement[i].ValidityPeriod.EndTime) < Date.parse(dateTime) || doc.data().PtSituationElement[i].Progress === 'closed'){
                                console.log('ignore');
                            }else{
                                var swapPlaces = doc.data().PtSituationElement[i];
                                var tmp = {};
                                tmp['CreationTime'] = swapPlaces.CreationTime;
                                tmp['ParticipantRef'] = swapPlaces.ParticipantRef;
                                tmp['SituationNumber'] = swapPlaces.SituationNumber;
                                tmp['Source'] = swapPlaces.Source;
                                tmp['Progress'] = swapPlaces.Progress;
                                tmp['ValidityPeriod'] = swapPlaces.ValidityPeriod;
                                tmp['Severity'] = swapPlaces.Severity;
                                tmp['ReportType'] = swapPlaces.ReportType;
                                tmp['Summary'] = swapPlaces.Summary;
                                if(swapPlaces.Description){ tmp['Description'] = swapPlaces.Description; }
                                tmp['Affects'] = swapPlaces.Affects;
                                if(swapPlaces.Affects.Networks){
                                    var stopPoints = {StopPoints: []};
                                    console.log(swapPlaces.Affects.Networks.AffectedNetwork.AffectedLine.Routes.AffectedRoute)
                                    stopPoints.StopPoints.push(swapPlaces.Affects.Networks.AffectedNetwork.AffectedLine.Routes.AffectedRoute);
                                    console.log(stopPoints)
                                    tmp.Affects.Networks.AffectedNetwork.AffectedLine.Routes = stopPoints;
                                }

                                situations.PtSituationElement.push(tmp)
                                console.log(situations)
                            }
                        }
                        array.SituationExchangeDelivery.Situations.push(situations);
                        siri.Siri.ServiceDelivery = array;

                        var result = convert.js2xml(siri, {compact: true, spaces: 4});
                        console.log(result);
                    }else{
                        this.setState({
                            data: {PtSituationElement: []},
                            id: doc.id,
                        })
                    }
                }
            });
        });

        api.run(this.state.key, null, null)
            .then(response => {
                this.setState({
                    lines: response.data.lines
                })
            })

    }

    render() {
        return (
            <Router>
                <div>
                    <Route path="/" render={props => <Home {...props} user={this.state.key.split(':')[0]} logout={this.props.userInfo.logoutUrl} />} />
                    <Route path="/edit/:deviationId?" render={props =>
                        <Edit {...props} data={this.state.data} firebase={db} docID={this.state.id}
                              orgID={this.state.key} user={this.state.key.split(':')[0]} logout={this.props.userInfo.logoutUrl} />} />
                    <Route path="/register" render={props =>
                        <Register {...props} data={this.state.data} firebase={db} docID={this.state.id}
                                  orgID={this.state.key} lines={this.state.lines} user={this.state.key.split(':')[0]} logout={this.props.userInfo.logoutUrl} />} />
                    <Route path="/overview" render={props => <Overview {...props} data={this.state.data}
                                                                       user={this.state.key.split(':')[0]} logout={this.props.userInfo.logoutUrl} />} />
                </div>
            </Router>
        )
    }
}

const renderIndex = (userInfo) => {
    ReactDOM.render(
        <App userInfo={userInfo} />,
        document.getElementById('root')
    );
};

renderIndex("null");
//auth.initAuth();

export default renderIndex;
