import React from "react";

const { createApolloFetch } = require('apollo-fetch');

const uri = 'https://api.staging.entur.io/journey-planner/v2/graphql';
const uri2 = 'https://api.staging.entur.io/journey-planner/v2/graphql';

const query = `
  {
  lines(authorities:"GOA:Authority:GOA") {
  	id
    name
    quays {
      id
      name
    }
  }
}
`;


const variables = {
    authorities: localStorage.getItem("loggedInAs")+':Authority:GOA'+localStorage.getItem("loggedInAs"),
};

const run = () => {
    const apolloFetch = createApolloFetch({ uri });
    apolloFetch({ query, variables })
        .catch(error => error)
        .then(response => {
            localStorage.setItem('lines', JSON.stringify(response.data.lines));
        });
};

export default { run }
