import React from "react";
const { createApolloFetch } = require('apollo-fetch');

const uri = 'https://api.staging.entur.io/journey-planner/v2/graphql';

const run = (localKey, authorities, date) => {
    const apolloFetch = createApolloFetch({ uri });
    let dates = null;
    if(date === null){
        date = '';
    }else{
        dates = ', activeDates: "'+date+'"';
    }

    const query = `
      {
      lines(authorities:"${authorities}"${dates}) {
        id
        name
        quays {
          id
          name
        }
      }
    }
    `;
    console.log(query);

    apolloFetch({ query })
        .catch(error => error)
        .then(response => {
            if(date === null){
                localStorage.setItem(localKey, JSON.stringify(response.data.lines));
            }else{
                localStorage.setItem(localKey, JSON.stringify(response));
            }
        });
};

export default { run }
