import React from 'react';
import ReactDOM from 'react-dom';
import './style/index.css';
import 'bootstrap/dist/css/bootstrap.css';
import { Route, BrowserRouter as Router } from 'react-router-dom'
import Home from './components/home'
import Overview from "./components/overview";
import Register from './components/register';
import Edit from './components/edit';
import auth from './authenticate/auth'
import issues from "./data";
import api from "./api/api";

const routing = (
    <Router>
        <div>
            <Route path="/" component={Home} />
            <Route path="/edit/:deviationId?" component={Edit} />
            <Route path="/register" component={Register} />
            <Route path="/overview" component={Overview} />
        </div>
    </Router>
);

const renderIndex = (userInfo) => {
    if(!localStorage.getItem("issues")){
        localStorage.setItem('issues', JSON.stringify(issues));

        api.organisationID(userInfo.id)
            .then(data => {
                localStorage.setItem('netexAuthorityId', data.references.netexAuthorityId);
                const authorities = localStorage.getItem("netexAuthorityId");
                api.run(authorities, null, null)
                    .then(data => {
                        localStorage.setItem("lines", JSON.stringify(data.lines))

                    });

            });
    }
    setTimeout(() => {
        ReactDOM.render(
            routing,
            document.getElementById('root')
        );
    }, 800);
};

auth.initAuth();

export default renderIndex
