import React from "react";

const { createApolloFetch } = require('apollo-fetch');

const uri2 = 'https://api.staging.entur.io/journey-planner/v2/graphql';

const query =
    `mutation PostForLines($authorities: String!) {
        lines(authorities: $authorities) {
            id
            name
            quays {
                id
                name
            }
        }
    }
`;

const variables = {
    authorities: "GOA:Authority:GOA",
};

const run = (localKey, uri) => {
    window.alert(variables);
    const apolloFetch = createApolloFetch({ uri });

    apolloFetch({ query, variables })
        .catch(error => error)
        .then(response => {
            localStorage.setItem(localKey, JSON.stringify(response.data.lines));
        });
};

export default { run }
