const client_id = 'knJ9iltoiODFZ9QDpS5KXtBC547uLUzu';
const client_secret = 'ly0IUXDzhBpgMCdN32p6uSZeoh2NGg8fIveqDdYO68WE8mQ4HKCqQygRnESy4sAW';
const auth0_domain = 'internal.staging.entur.org';
const auth0_aduience = 'api.staging.entur.io';

const authorization = () => {
    return fetch('https://'+auth0_domain+'/oauth/token', {
        method: 'POST',
        headers: {
            'Content-type': 'application/json'
        },
        body: {
            'grant_type': "client_credentials",
            "client_id": client_id,
            "client_secret": client_secret,
            "audience": 'https://' + auth0_aduience
        }
    })
        .catch(error => error)
        .then(response => response);
};

export default{ authorization };
