const { createApolloFetch } = require('apollo-fetch');

const uri = 'https://api.staging.entur.io/journey-planner/v2/graphql';

const run = (authorities, line, date) => {
    const apolloFetch = createApolloFetch({ uri });
    const lines = (date === null) ? '' : ', lines: "'+line+'"';
    const dates = (date === null) ? '' : ', activeDates: "'+date+'"';
    const partQuay = (date === null) ? '\n name\n quays {\n id\n name\n }' :
        '\n estimatedCalls(date:"'+date+'") {\n aimedDepartureTime\n quay {\n id\n name\n }\n }';
    const type = (date === null) ? 'lines' : 'serviceJourneys';

    const query = `{ ${type}(authorities:"${authorities}"${lines}${dates}) { id${partQuay} } } `;

    apolloFetch({ query })
        .catch(error => error)
        .then(response => response)
};

const fetchGet = {
    method: 'GET',
    mode: 'no-cors',
    headers: {
        'Content-type': 'application/json',
    }
};

const organisationID = (id) => {
    fetch('https://api.staging.entur.io/organisations/v1/register/organisations/'+id, fetchGet)
        .catch (error => error)
        .then(data => {
            window.alert(JSON.parse(data));
        })
};

export default { run, organisationID }
