import React from 'react';
import ReactDOM from 'react-dom';
import './style/index.css';
import 'bootstrap/dist/css/bootstrap.css';
import { Route, BrowserRouter as Router } from 'react-router-dom'
import Home from './components/home'
import Overview from "./components/overview";
import Register from './components/register';
import Edit from './components/edit';
import auth from './authenticate/auth'
import api from "./api/api";


const firebase = require("firebase");
require("firebase/firestore");
var firebaseConfig = {
    apiKey: "AIzaSyCKcRzWRYlZkwG4I4WTTb2uRnI7VczCX5M",
    authDomain: "deviation-messages.firebaseapp.com",
    databaseURL: "https://deviation-messages.firebaseio.com",
    projectId: "deviation-messages",
    storageBucket: "deviation-messages.appspot.com",
    messagingSenderId: "132641363074",
    appId: "1:132641363074:web:1069e5f0a9717b4c"
};
firebase.initializeApp(firebaseConfig);
var db = firebase.firestore();


class App extends React.Component {
    state = {
        data: '',
        key: 'GOA:Authority:GOA',
        lines: '',
    };

    componentDidUpdate(data) {
        localStorage.setItem('issues', JSON.stringify(this.state.data.issues));
    }

    componentDidMount() {
        localStorage.setItem('netexAuthorityId', 'GOA:Authority:GOA');

        //api.organisationID(userInfo.id);

        db.collection(this.state.key).get().then((querySnapshot) => {
            querySnapshot.forEach((doc) => {
                this.setState({
                     data: doc,
                })
            });
        });

        const authorities = localStorage.getItem("netexAuthorityId");
        api.run('lines', authorities, null, null)
            .then(response => {
                this.setState({
                    lines: JSON.stringify(response.data.lines)
                })
            })
    }

    render() {
        return (
            <Router>
                <div>
                    <Route path="/" component={Home} />
                    <Route path="/edit/:deviationId?" render={props => <Edit {...props} data={this.state.data} firebase={db} />} />
                    <Route path="/register" component={Register} />
                    <Route path="/overview" render={props => <Overview {...props} data={this.state.data}/>}  />
                </div>
            </Router>
        )
    }
}

const renderIndex = (userInfo) => {
    ReactDOM.render(
        <App userInfo={userInfo} />,
        document.getElementById('root')
    );
};

auth.initAuth();

export default renderIndex;
