import React from "react";
import Background from "../img/background.jpg";
import NavBar from "./navbar";

class Edit extends React.Component {

    state = {
        id: 1,
        date: '',
    };

    componentDidMount() {
        let that = this;
        let date = new Date().getDate(); if(date < 10){ date = "0"+date }
        let month = new Date().getMonth() + 1; if(month < 10){ month = "0"+month }
        let year = new Date().getFullYear();
        let hours = new Date().getHours(); if(hours < 10){ hours = "0"+hours }
        let min = new Date().getMinutes(); if(min < 10){ min = "0"+min }
        let sec = new Date().getSeconds(); if(sec < 10){ sec = "0"+sec }

        that.setState({
            date: year + '-' + month + '-' + date +'T' + hours + ':' + min + ':' + sec+"+02:00",
        });

        if(this.props.location.state){
            this.setState({
                id: this.props.location.state.id,
            })
        }
        console.log(this.state.date.replace(":00+02:00", ""))
    }

    handleSubmit = (event) => {
        // if(Date.parse(event.target.to.value) < Date.parse(this.state.date)){}
        let issues = JSON.parse(localStorage.getItem("issues"));
        issues.PtSituationElement[(parseInt(this.state.id)-1)].Progress = 'open';
        issues.PtSituationElement[(parseInt(this.state.id)-1)].Summary['@Value'] = event.target.oppsummering.value;
        issues.PtSituationElement[(parseInt(this.state.id)-1)].Description['@Value'] = event.target.beskrivelse.value;
        issues.PtSituationElement[(parseInt(this.state.id)-1)].ValidityPeriod.StartTime = event.target.from.value;
        issues.PtSituationElement[(parseInt(this.state.id)-1)].ValidityPeriod.EndTime = event.target.to.value;
        localStorage.setItem('issues', JSON.stringify(issues));
        this.props.history.push('/overview');
    };

    handleClick = () => {
        this.props.history.push('/overview');
    };

    setProgressToClosed = () => {
        let issues = JSON.parse(localStorage.getItem("issues"));
        issues.PtSituationElement[(parseInt(this.state.id)-1)].Progress = 'closed';
        localStorage.setItem('issues', JSON.stringify(issues));
        this.props.history.push('/overview');
    };


    checkStatus = (param) => {
        if(param === 'open'){
            return <div className="submit justify-content-center">
                <button onClick={this.setProgressToClosed} className="btn btn-danger btn-lg btn-block">Deaktiver</button>
                <button type="submit" className="btn btn-primary btn-lg btn-block">Endre</button>
                <button onClick={this.handleClick} type="submit" className="btn btn-warning btn-lg btn-block">Tilbake</button></div>
        }else{
            return <div className="submit justify-content-center">
                <button className="btn btn-success btn-lg btn-block">Aktiver</button>
                <button onClick={this.handleClick} type="submit" className="btn btn-warning btn-lg btn-block">Tilbake</button></div>
        }
    };

    render() {
        return (
            <div>
                <img id="background" src={ Background } alt="" />
                <div className="navBar">
                </div>
                <NavBar />
                <div className="register_box" id="overview" >
                    <form className="register" onSubmit={this.handleSubmit}>
                        <h2 className="text-center text-white">Endre avvik</h2>
                        <br></br>
                        <p className="text-center text-white">Melding</p>
                        <input type="String" name="oppsummering" className="form-control"
                               defaultValue={JSON.parse(localStorage.getItem("issues")).PtSituationElement[(parseInt(this.props.location.state.id)-1)].Summary['@Value']}
                                required/>
                        <input type="String" name="beskrivelse" className="form-control"
                               defaultValue={JSON.parse(localStorage.getItem("issues")).PtSituationElement[(parseInt(this.props.location.state.id)-1)].Description['@Value']}/>
                        <br></br>
                        <p className="text-center text-white">Gyldighetsdato</p>
                        <div className="form-group d-flex">
                            <input type="datetime-local" name="from" className="form-control"
                                   defaultValue={JSON.parse(localStorage.getItem("issues")).PtSituationElement[(parseInt(this.props.location.state.id)-1)].ValidityPeriod.StartTime.replace(":00+02:00", "")}/>
                            <input onChange={this.handleChangeDate} type="datetime-local" name="to" className="form-control"
                                   defaultValue={JSON.parse(localStorage.getItem("issues")).PtSituationElement[(parseInt(this.props.location.state.id)-1)].ValidityPeriod.EndTime.replace(":00+02:00", "")}
                                   min={this.state.date.replace(":00+02:00", "")}
                                   required/>
                        </div>
                        <br></br>
                        {this.checkStatus(JSON.parse(localStorage.getItem("issues")).PtSituationElement[(parseInt(this.props.location.state.id)-1)].Progress)}
                    </form>
                </div>
            </div>
        );
    }
}

export default Edit;
