import React from 'react';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.css';
import './style/index.css';
import { Route, BrowserRouter as Router } from 'react-router-dom'
import Overview from "./components/overview";
import Register from './components/register';
import Edit from './components/edit';
import auth from './authenticate/auth';
import api from "./api/api";

var config = {
    apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
    authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
    databaseURL: process.env.REACT_APP_FIREBASE_DATABASE_URL,
    projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
    storageBucket: process.env.REACT_APP_FIREBASE_STORAGEBUCKET,
    messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
    appId: process.env.REACT_APP_TMDB_API_KEY
};

const firebase = require("firebase/app");
require("firebase/firestore");

firebase.initializeApp(config);
var db = firebase.firestore();

class App extends React.Component {
    state = {
        data: '',
        chosenOrg: "GOA:Authority:GOA",
        allOrg: [],
        lines: '',
        id: '',
    };

    componentDidMount() {
        let roles = this.props.userInfo.roles;
        let setRoles = [];
        for (let i = 0; i < roles.length; i++) {
            if(roles[i].includes('editSX')) {
                setRoles.push(roles[i].split("\"")[7]);
            }
        }
        api.getAuthorities()
          .then(response => {
              const authorities = response.data.authorities;
              for (let i = 0; i < setRoles.length; i++) {
                  for (let j = 0; j < authorities.length; j++) {
                      if(authorities[j].id.includes(setRoles[i])){
                          setRoles[i] = authorities[j].id;
                      }
                  }
              }
              this.setState({
                  chosenOrg: setRoles[0],
                  allOrg: setRoles,
              });
          });

        setTimeout(function() {

            db.collection(this.state.chosenOrg).get().then((querySnapshot) => {
                querySnapshot.forEach((doc) => {
                    if(doc.id === 'Issues'){
                        if(doc.data().PtSituationElement){
                            this.setState({
                                data: doc.data(),
                                id: doc.id,
                            });
                        }else{
                            this.setState({
                                data: {PtSituationElement: []},
                                id: doc.id,
                            })
                        }
                    }
                });
            });

            api.getLines(this.state.chosenOrg)
                .then(response => {
                    if(response.data){
                        this.setState({
                            lines: response.data.lines
                        })
                    }else{
                        console.log("Could not find any lines for this organization");
                    }
                })

            }, 1000);

    }

    render() {
        return (
            <Router>
                <div>
                    <Route path="/"
                           render={props => <Overview {...props}
                                                      firebase={db}
                                                      data={this.state.data}
                                                      user={this.state.chosenOrg.split(':')[0]}
                                                      logout={this.props.userInfo.logoutUrl} />} />
                    <Route path="/edit/:deviationId?"
                           render={props => <Edit {...props}
                                                  data={this.state.data}
                                                  firebase={db}
                                                  docID={this.state.id}
                                                  orgID={this.state.chosenOrg}
                                                  user={this.state.chosenOrg.split(':')[0]}
                                                  ogout={this.props.userInfo.logoutUrl} />} />
                    <Route path="/register"
                           render={props => <Register {...props}
                                                      data={this.state.data}
                                                      firebase={db}
                                                      docID={this.state.id}
                                                      orgID={this.state.chosenOrg}
                                                      lines={this.state.lines}
                                                      user={this.state.chosenOrg.split(':')[0]}
                                                      logout={this.props.userInfo.logoutUrl} />} />
                </div>
            </Router>
        )
    }
}

const renderIndex = (userInfo) => {
    ReactDOM.render(
        <App userInfo={userInfo} />,
        document.getElementById('root')
    );
};

auth.initAuth();

export default renderIndex;
