import React from 'react';
import ReactDOM from 'react-dom';
import './style/index.css';
import 'bootstrap/dist/css/bootstrap.css';
import { Route, BrowserRouter as Router } from 'react-router-dom'
import Home from './components/home'
import Overview from "./components/overview";
import Register from './components/register';
import Edit from './components/edit';
import auth from './authenticate/auth'
import api from "./api/api";
import WebFont from 'webfontloader';

WebFont.load({
    google: {
        families: ['Titillium Web:300,400,700', 'Nationale light']
    }
});

require('dotenv').config();

const firebase = require("firebase/app");
require("firebase/firestore");

console.log(process.env);

var firebaseConfig = {
    apiKey: 'AIzaSyCKcRzWRYlZkwG4I4WTTb2uRnI7VczCX5M',
    authDomain: 'deviation-messages.firebaseapp.com',
    databaseURL: 'https://deviation-messages.firebaseio.com',
    projectId: 'deviation-messages',
    storageBucket: 'deviation-messages.appspot.com',
    messagingSenderId: '132641363074',
    appId: '1:132641363074:web:1069e5f0a9717b4c'
};

var firebaseConfig2 = {
    apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
    authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
    databaseURL: process.env.REACT_APP_FIREBASE_DATABASE_URL,
    projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
    storageBucket: process.env.REACT_APP_FIREBASE_STORAGEBUCKET,
    messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
    appId: process.env.REACT_APP_TMDB_API_KEY
};

firebase.initializeApp(firebaseConfig);
var db = firebase.firestore();

class App extends React.Component {
    state = {
        data: '',
        key: 'GOA:Authority:GOA',
        lines: '',
        id: '',
    };

    componentDidMount() {
        //api.organisationID(userInfo.id);

        db.collection(this.state.key).get().then((querySnapshot) => {
            querySnapshot.forEach((doc) => {
                if(doc.id === 'Issues'){
                    if(doc.data().PtSituationElement){
                        this.setState({
                            data: doc.data(),
                            id: doc.id,
                        });
                    }else{
                        this.setState({
                            data: {PtSituationElement: []},
                            id: doc.id,
                        })
                    }
                }
            });
        });

        api.run(this.state.key, null, null)
            .then(response => {
                this.setState({
                    lines: response.data.lines
                })
            })

    }

    render() {
        return (
            <Router>
                <div>
                    <Route path="/" render={props => <Home {...props} user={this.state.key.split(':')[0]} logout={this.props.userInfo.logoutUrl} />} />
                    <Route path="/edit/:deviationId?" render={props =>
                        <Edit {...props} data={this.state.data} firebase={db} docID={this.state.id}
                              orgID={this.state.key} user={this.state.key.split(':')[0]} logout={this.props.userInfo.logoutUrl} />} />
                    <Route path="/register" render={props =>
                        <Register {...props} data={this.state.data} firebase={db} docID={this.state.id}
                                  orgID={this.state.key} lines={this.state.lines} user={this.state.key.split(':')[0]} logout={this.props.userInfo.logoutUrl} />} />
                    <Route path="/overview" render={props => <Overview {...props} data={this.state.data}
                                                                       user={this.state.key.split(':')[0]} logout={this.props.userInfo.logoutUrl} />} />
                </div>
            </Router>
        )
    }
}

const renderIndex = (userInfo) => {
    ReactDOM.render(
        <App userInfo={userInfo} />,
        document.getElementById('root')
    );
};

renderIndex("null");
//auth.initAuth();

export default renderIndex;
