import React from "react";
import gql from 'graphql-tag';
const { createApolloFetch } = require('apollo-fetch');

const uri2 = 'https://api.staging.entur.io/journey-planner/v2/graphql';

const query = `
  {
  lines(authorities:"`+`GOA:Authority:GOA") {
  	id
    name
    quays {
      id
      name
    }
  }
}
`;


const variables = {
    authorities: localStorage.getItem("loggedInAs")+':Authority:'+localStorage.getItem("loggedInAs"),
};

const run = (localKey, uri) => {
    const apolloFetch = createApolloFetch({ uri });

    apolloFetch({ query2, variables })
        .catch(error => error)
        .then(response => {
            localStorage.setItem(localKey, JSON.stringify(response.data.lines));
        });
};

const query2 = `
    mutation lines($authorities: String!) {
        lines(authorities: $authorities) {
            id
            name
            quays {
                id
                name
            }
        }
    }
`;

export default { run }
