import cx from 'classnames';

export function getClassNames(baseClassName, { className, variant, size, width, active, disabled, loading }) {
  return cx(baseClassName, {
    [`${baseClassName}--variant-${variant}`]: variant,
    [`${baseClassName}--size-${size}`]: size,
    [`${baseClassName}--width-${width}`]: width,
    [`${baseClassName}--active`]: active,
    [`${baseClassName}--disabled`]: disabled,
    [`${baseClassName}--loading`]: loading,
  }, className);
}

export function getFocusClassName(baseClassName) {
  return `${baseClassName}--has-focus`;
}
