import React from 'react';
import cx from 'classnames';
import PropTypes from 'prop-types';

import Chevron from '../../icons/arrows/chevron';

import { rootClassName } from './styles.scss';

export function ExpandableHeader({
  className,
  children,
  expanded,
  hideArrowIcon,
  onClick,
  variant,
}) {
  const classNames = cx(rootClassName, className, {
    [`${rootClassName}--variant-${variant}`]: variant,
  });
  const arrowDirection = expanded ? 'up' : 'down';
  return (
    <div className={classNames} onClick={onClick}>
      {children}
      {!hideArrowIcon && (
        <Chevron
          className={`${rootClassName}__arrow`}
          direction={arrowDirection}
        />
      )}
    </div>
  );
}

ExpandableHeader.propTypes = {
  children: PropTypes.node.isRequired,
  className: PropTypes.string,
  expanded: PropTypes.bool,
  hideArrowIcon: PropTypes.bool,
  onClick: PropTypes.func,
  variant: PropTypes.oneOf(['primary', 'midnight']),
};
