import React from 'react';
import PropTypes from 'prop-types';
import { getIconSize } from '../helper';

function directionTransform(direction) {
  switch (direction) {
    case 'down':
      return 'rotate(270), translate(-16, 0)';
    case 'up':
      return 'rotate(90), translate(0, -16)';
    case 'right':
      return 'rotate(180), translate(-16, -16)';
  }
}

function Arrow({ color = 'currentColor', size, direction, ...svgProps }) {
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 16 16"
      {...getIconSize(size)}
      {...svgProps}
    >
      <polygon
        fill={color}
        transform={directionTransform(direction)}
        points="6.7002,14.39977 8.1001,13.00035 3.7998,8.70006 16,8.70006 16,6.70006 3.7998,6.70006
		8.1001,2.39977 6.7002,0.99986 0,7.70006"
      />
    </svg>
  );
}

Arrow.propTypes = {
  size: PropTypes.oneOf(['xlarge', 'large', 'medium', 'small']),
  color: PropTypes.string,
  direction: PropTypes.oneOf(['up', 'down', 'left', 'right']),
};

export default Arrow;
