import React from 'react';
import PropTypes from 'prop-types';
import { getIconSize } from '../helper';

function directionTransform(direction) {
  switch (direction) {
    case 'up':
      return 'rotate(270) translate(-9, -2)';
    case 'down':
      return 'rotate(90) translate(2, -9)';
    case 'left':
      return 'rotate(180) translate(-7, -11)';
  }
}

function Chevron({ color = 'currentColor', size, direction, ...svgProps }) {
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 7 11"
      { ...getIconSize(size) }
      { ...svgProps }
    >
      <path
        fill={ color }
        transform={ directionTransform(direction) }
        d="M1.399802 0L0 1.3748785l4.200396 4.1256077L0 9.6251215 1.399802 11 7 5.5004862 5.600198 4.1256077z"
      />
    </svg>
  );
}

Chevron.propTypes = {
  size: PropTypes.oneOf(['xlarge', 'large', 'medium', 'small']),
  color: PropTypes.string,
  direction: PropTypes.oneOf(['up', 'down', 'left', 'right']),
};

export default Chevron;
