import React from 'react';
import PropTypes from 'prop-types';
import cx from 'classnames';
import { rootClassName } from './styles.scss';

export function Label(props) {
  const { children, className, variant, ...rest } = props;
  const combinedClassNames = cx(rootClassName, {
    [`${rootClassName}--variant-midnight`]: variant === 'midnight',
  }, className);

  return (
    <label {...rest} className={combinedClassNames}>
      {children}
    </label>
  );
}

export function Legend(props) {
  const { children, className, variant, ...rest } = props;
  const combinedClassNames = cx(rootClassName, {
    [`${rootClassName}--variant-midnight`]: variant === 'midnight',
  }, className);

  return (
    <legend {...rest} className={combinedClassNames}>
      {children}
    </legend>
  );
}

Label.propTypes = Legend.propTypes = {
  className: PropTypes.string,
  children: PropTypes.node,
  variant: PropTypes.oneOf(['primary', 'midnight']),
};
