import React from 'react';
import PropTypes from 'prop-types';
import cx from 'classnames';

import { rootClassName } from './styles.scss';

function getClassNames(baseClassName, { className, variant }) {
  return cx(baseClassName, {
    [`${baseClassName}--variant-${variant}`]: variant,
  }, className);
}

export default function LoadingSpinner( { variant, className } ) {
  const classNames = getClassNames(rootClassName, { className, variant });
  return  (
    <div className={classNames}/>
  );
}

LoadingSpinner.propTypes = {
  className: PropTypes.string,
  variant: PropTypes.string,
};
