import React from 'react';
import PropTypes from 'prop-types';
import cx from 'classnames';

import { rootClassName } from './styles.scss';

export function StickyHeader(props) {
  const {
    headerElement: HeaderElement = 'h2',
    className,
    children,
  } = props;

  return (
    <HeaderElement className={ cx(rootClassName, className) }>
      <span className={`${rootClassName}__label`}>{ children }</span>
    </HeaderElement>
  );
}


StickyHeader.propTypes = {
  children: PropTypes.node,
  className: PropTypes.string,
  headerElement: PropTypes.string,
};
