import React from 'react';
import PropTypes from 'prop-types';
import cx from 'classnames';

import { rootClassName } from './styles.scss';

export function SubLabel(props) {
  const { children, className, ...rest } = props;
  const combinedClassNames = cx(rootClassName, className);

  return (
    <label {...rest} className={combinedClassNames}>
      {children}
    </label>
  );
}

export function Legend(props) {
  const { children, className, ...rest } = props;
  const combinedClassNames = cx(rootClassName, className);

  return (
    <legend {...rest} className={combinedClassNames}>
      {children}
    </legend>
  );
}

SubLabel.propTypes = Legend.propTypes = {
  className: PropTypes.string,
  children: PropTypes.node,
};
