import React from 'react';
import PropTypes from 'prop-types';
import cx from 'classnames';

import CloseIcon from '../icons/ui/close';
import { rootClassName } from './styles.scss';

export function TravelTag({
  icon, label, className, variant, onClose,
}) {
  const classNames = cx(rootClassName, {
    [`${rootClassName}--variant-midnight`]: variant,
  }, className);

  return (
    <div className={ classNames }>
      { icon }

      { label &&
        <span className={`${rootClassName}__label`}>
          { label }
        </span>
      }

      { onClose &&
        <button onClick={ onClose } className={`${rootClassName}__close-button`}>
          <CloseIcon width="12" height="12" />
        </button>
      }
    </div>
  );
}

TravelTag.propTypes = {
  className: PropTypes.string,
  icon: PropTypes.node.isRequired,
  label: PropTypes.string,
  onClose: PropTypes.func,
  variant: PropTypes.oneOf(['midnight']),
};
